mapper.select:
SELECT * FROM CHAT_MESSAGES
WHERE CHAT_ROOM_NO = #{chatRoomNo}
ORDER BY CHAT_NO

mapper.update:
UPDATE CHAT_MESSAGE SET
CHAT_READ_YN = 'Y'
WHERE CHAT_ROOM_NO = #{chatRoomNo}
AND MEMBER_NO  <>  #{memberNo}

mapper.select:
SELECT MEMBER_NO FROM CHAT_ROOM
WHERE CHAT_ROOM_NO = #{chatRoomNo}
AND MEMBER_NO  <>  #{memberNo}
